CREATE TABLE civilians (
    id BIGINT AUTO_INCREMENT PRIMARY KEY,
    full_name VARCHAR(255),
    nic_number VARCHAR(255) UNIQUE,
    email VARCHAR(255) UNIQUE NOT NULL,
    phone VARCHAR(255),
    password_hash VARCHAR(255),
    account_status ENUM('ACTIVE', 'TEMP_BANNED', 'PERMANENT_BANNED'),
    temp_ban_count INT DEFAULT 0,
    appeal_count INT DEFAULT 0,
    ban_date DATETIME,
    permanent_ban_date DATETIME,
    masked_email VARCHAR(255),
    masked_name VARCHAR(255),
    is_login_disabled BOOLEAN DEFAULT FALSE,
    ban_reason TEXT,
    created_at DATETIME,
    updated_at DATETIME
);

